"use strict";

var buildNode = require('../utils/buildNode');

var _require = require('datocms-client'),
    seoTagsBuilder = _require.seoTagsBuilder;

module.exports = function buildSeoMetaTagsNode(itemNode, itemEntity, entitiesRepo, i18n) {
  return buildNode('DatoCmsSeoMetaTags', itemNode.id, function (node) {
    node.tags = seoTagsBuilder(itemEntity, entitiesRepo, i18n);
  });
};